import React, { Component } from 'react';
import MagicCards from '../MagicCards/MagicCards'
import '../App.css';
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  DropdownMenu,
  DropdownItem } from 'reactstrap';

export default class MainContainer extends Component {
    state = {
        magicCards: [],
        collapsed: true
    }
    toggleNavbar = this.toggleNavbar.bind(this);
    getMagicCards = async () => {
        try {
            const magicApi = await fetch('https://api.scryfall.com/cards/search?order=set&q=e%3Arna&unique=prints')
            const magicData = await magicApi.json()

            this.setState({ magicCards: magicData.data })
        } catch (err) {
            console.log(err, 'catch caught an error')
            return err
        }

    }
    toggleNavbar() {
      this.setState({
        collapsed: !this.state.collapsed
      });
    }
    componentDidMount() {
        // this.setState((previousState)=>{
        //     pokemons:previousState.pokemons.filter((pokemons,i)=> i !==index)
        //   }
        // )
        this.getMagicCards().then((data) => console.log(data, 'gotemAll'))
    }
    render() {
        return (
        <div>
          <section className="transBg">
            <h1>Deck Builder</h1>
            <img src="https://zone1-vgu.netdna-ssl.com/wp-content/uploads/2019/01/MagicThe-Gathering-Ravnica-Allegiance-New-Mechanics-Breakdown.jpg" alt="hero" className="heroBg"/>
          </section>
          <Navbar color="faded" light>
          <NavbarBrand href="/" className="mr-auto">reactstrap</NavbarBrand>
          <NavbarToggler onClick={this.toggleNavbar} className="mr-2" />
          <Collapse isOpen={!this.state.collapsed} navbar>
            <Nav navbar>
              <NavItem>
                <NavLink href="/components/">Components</NavLink>
              </NavItem>
              <NavItem>
                <NavLink href="https://github.com/reactstrap/reactstrap">GitHub</NavLink>
              </NavItem>
            </Nav>
          </Collapse>
        </Navbar>
          <MagicCards magicCards={this.state.magicCards}/>
      </div>
        );
    }
}
