import React from 'react'
import { Image } from 'semantic-ui-react'
const ImageExampleCircular = () => (
  <Image src='/images/wireframe/square-image.png' size='medium' circular />
)
const magicList = (props)=>{
	const cardsList = props.magicCards.map((magicCards, i)=>{
		return <li key={i} className="three wide column">{magicCards.name}, ({magicCards.set}), {magicCards.rarity}<img src={magicCards.image_uris.large} alt='magic cards' className='enlarge ui medium circular image'/><button onClick={props.addCard.bind(null, i)}>Add to deck</button></li>
	})
	return(
		<div>
			<ul className = "ui grid">
				{cardsList}
			</ul>

		</div>
	)
}

export default magicList
