import React, { Component } from 'react';
import MagicCards from '../MagicCards/MagicCards'
import '../App.css';
import { Nav, NavItem, Dropdown, DropdownItem, DropdownToggle, DropdownMenu, NavLink } from 'reactstrap';


export default class MainContainer extends Component {
    state = {
        magicCards: [],
        collapsed: true
    }
    toggleNavbar = this.toggleNavbar.bind(this);
    getMagicCards = async () => {
        try {
            const magicApi = await fetch('https://api.scryfall.com/cards/search?order=set&q=e%3Arna&unique=prints')
            const magicData = await magicApi.json()

            this.setState({ magicCards: magicData.data })
        } catch (err) {
            console.log(err, 'catch caught an error')
            return err
        }

    }
    toggleNavbar() {
      this.setState({
        collapsed: !this.state.collapsed
      });
    }
    componentDidMount() {
        // this.setState((previousState)=>{
        //     pokemons:previousState.pokemons.filter((pokemons,i)=> i !==index)
        //   }
        // )
        this.getMagicCards().then((data) => console.log(data, 'gotemAll'))
    }
    render() {
        return (
        <div>
          <section className="transBg">
            <h1>Deck Builder</h1>
            <img src="https://zone1-vgu.netdna-ssl.com/wp-content/uploads/2019/01/MagicThe-Gathering-Ravnica-Allegiance-New-Mechanics-Breakdown.jpg" alt="hero" className="heroBg"/>
          </section>
          <Nav pills>
          <NavItem>
            <NavLink href="#" active>Link</NavLink>
          </NavItem>
          <Dropdown nav isOpen={this.state.dropdownOpen} toggle={this.toggle}>
            <DropdownToggle nav caret>
              Dropdown
            </DropdownToggle>
            <DropdownMenu>
              <DropdownItem header>Header</DropdownItem>
              <DropdownItem disabled>Action</DropdownItem>
              <DropdownItem>Another Action</DropdownItem>
              <DropdownItem divider />
              <DropdownItem>Another Action</DropdownItem>
            </DropdownMenu>
          </Dropdown>
          <NavItem>
            <NavLink href="#">Link</NavLink>
          </NavItem>
          <NavItem>
            <NavLink href="#">Another Link</NavLink>
          </NavItem>
          <NavItem>
            <NavLink disabled href="#">Disabled Link</NavLink>
          </NavItem>
        </Nav>  
          <MagicCards magicCards={this.state.magicCards}/>
      </div>
        );
    }
}
