import React from 'react';

const postUsersDeck = async (props) => {
        const data = this.state.cardsAdded
        try {
            const addCard = await fetch('http://localhost:9000/addCard', {
                method: "POST", // *GET, POST, PUT, DELETE, etc.
                headers: {
                    "Content-Type": "application/json",
                    // "Content-Type": "application/x-www-form-urlencoded",
                },
                body: JSON.stringify({data}), // body data type must match "Content-Type" header
            })
            const addCardsJson = await addCard.json()
            this.setState({ cardsAdded:addCardsJson})
            //needed to turn your information into human readable data

            return addCardsJson
        } catch (err) {
            console.log(err, 'catch caught an error')
            return err
        }
    }
    const componentDidMount = ()=>{
        this.postUsersDeck().then((data) => console.log(data, 'POST ROUTE FULL OF MAAAGIC'))
    }
    const cardsAddedFunc = ()=>{
        this.state.cardsAdded.push()
}


export default postUsersDeck
