import React from 'react'
import MagicCards from '../MagicCards/MagicCards'
const UserDeck = (props)=>{
    addCard = (i)=>{
        const newUserDeck = this.state.usersDeck
        //magicCards[i] is accessing the bind's index (i) in MagicCards.js
        newUserDeck.push(this.state.magicCards[i])
        this.setState({
            usersDeck: newUserDeck
        })
    }
	//map over the deck with a new array and display the cards name in the deck section
	const newDeckArr = props.usersDeck.map((usersCards, i)=>{
		return <li className="cardWindow enlarge" key={i} onClick="deleteFromUserDeck"><img src={usersCards.image_uris.normal} alt='magic cards'/></li>
	})
	return(
		<div>

			<ul>
				{newDeckArr}
			</ul>
		</div>
	)
}

export default UserDeck
