import React from 'react'

const magicList = (props)=>{

	const addCard = ()=>{
		const newUserdeck = props.usersDeck
		newUserdeck.push(cardsList[0].name)
		console.log(newUserdeck)
	}

	const cardsList = props.magicCards.map((magicCards, i)=>{
		return <li key={i} className="three wide column">{magicCards.name}, ({magicCards.set}), {magicCards.rarity}<img src={magicCards.image_uris.large} alt='magic cards' className='enlarge ui fluid image'/><button onClick={addCard}>Add to deck</button></li>
	})
	return(
		<div>
			<ul className = "ui grid">
				{cardsList}
			</ul>

		</div>
	)
}

export default magicList
